(10, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'We\'re here for you - get tips, product updates and inspiration straight to your email box', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-07-04 23:32:30', NULL, 0, '', NULL, 0),
(11, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', 'speaker', '{}', 'unactioned', 'woocommerce-admin', '2020-07-04 23:32:30', NULL, 0, '', NULL, 0),
(12, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Give feedback', 'Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-10 16:39:19', NULL, 0, 'plain', '', 0),
(13, 'wc-admin-learn-more-about-product-settings', 'info', 'en_US', 'Learn more about Product Settings', 'In this video you\'ll find information about configuring product settings, such as how they are displayed, editing inventory options and so on.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-10 16:39:19', NULL, 0, 'plain', '', 0);
